    def get_movement_index(self):
        #  do we have enough exercises left?
        if not self.all_movements_indexes:  # if too few
            self.all_movements_indexes = [i for i in range(len(self.all_movements))]  # repopulate

        # returns random movement index
        return self.all_movements_indexes.pop(randint(0, len(self.all_movements_indexes) - 1))


    def get_compatible_movement(self, movements):
        
        # try to find a movement in remaining movement pool
        compatible_movements = []
        for mv1 in self.all_movements_indexes:
            #  is mv1 compatible with all movements
            compatibility_counter = 0
            for mv2 in movements:
                if self.movement_compatibility_matrix[mv1][mv2] == 1:
                    compatibility_counter += 1
        
            if compatibility_counter == len(movements):
                self.all_movements_indexes.remove(mv1)
                compatible_movements.append(mv1)
        
        if compatible_movements:
            return choice(compatible_movements)

        # repopulate if movement could not be found
        self.all_movements_indexes = [i for i in range(len(self.all_movements))]

        # try again with all movements
        for mv1 in self.all_movements_indexes:
            #  is mv1 compatible with all movements
            compatibility_counter = 0
            for mv2 in movements:
                if self.movement_compatibility_matrix[mv1][mv2] == 1:
                    compatibility_counter += 1
        
            if compatibility_counter == len(movements):
                self.all_movements_indexes.remove(mv1)
                compatible_movements.append(mv1)

        if compatible_movements:
            return choice(compatible_movements)

        # if no compatible movements are found, return None
        return None


    def generate_workout(self, num_ex):
        workout = []

        #  get random movement
        movement_index = self.get_movement_index()  # movement_index gets removed from all_movements_indexes

        #  add exercise_index to workout (1st movement)
        workout.append(movement_index)

        for _ in range(num_ex-1):

            # from compatible movements pool, pick a random movement and add it to the workout
            cmp_mvmnt = self.get_compatible_movement(workout)
            if cmp_mvmnt is not None:
                workout.append(cmp_mvmnt)

        # convert movement indices to names
        movements = [self.all_movements[mv] for mv in workout]

        if num_ex == 2 and randint(1, 2) == 2:
            cardio = self.cardio.getMovement()
            if cardio not in movements:
                movements.append(self.cardio.getMovement())

        return movements